import { useState } from "react";
import CakeList from "./CakeList.js";
// import Cake from "./Cake.js";
import CakeForm from "./CakeForm.js"

const CakeContainer = () => {

    const [listOfCakes, setListOfCakes] = useState(
		[
			{
				cakeName: "Victoria Sponge",
				   ingredients: [
					   "eggs",
					  "butter",
					  "sugar",
					  "self-raising flour",
					  "baking powder",
					  "milk"
				  ],
				  price: 5,
				   rating: 5,
				   id: 1
			},
			{
				 cakeName: "Tea Loaf",
				   ingredients: [
					   "eggs",
					  "oil",
					  "dried fruit",
					  "sugar",
					  "self-raising flour",
					  "strong tea",
				  ],
				  price: 2,
				  rating: 3,
				  id: 2

			},
			{
				 cakeName: "Carrot Cake",
				   ingredients: [
					"carrots",
					  "walnuts",
					  "oil",
					  "cream cheese",
					  "flour",
					  "sugar",
				   ],
				   price: 8,
				   rating: 5,
				   id: 3
			} 
		]		
	)

	const handleCakeSubmission=(newCake) => {
		const updatedCakes=[...listOfCakes]
		updatedCakes.push(newCake);
		setListOfCakes(updatedCakes);
	}

  return (
    <>
	<hr></hr>
        <h1>React Bakery</h1>
		<CakeForm onCakeSubmission={handleCakeSubmission}/>
		<CakeList cakes={listOfCakes}/>
    </>
  );
}

export default CakeContainer;
